import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { LoginformService } from 'src/app/services/auth/loginform.service';
import { loguinRequest } from 'src/app/services/auth/loguinRequest';

@Component({
  selector: 'app-form-login',
  templateUrl: './form-login.component.html',
  styleUrls: ['./form-login.component.css']
})
export class FormLoginComponent {
  //!aqui validamos los datos de entrada
  loginForm = this.fB.group({
    email: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.required]]
  })

  loguinError: string='';



  constructor(private fB: FormBuilder, private router: Router, private loginService: LoginformService) {

  }

  get email() {
    return this.loginForm.controls.email;
  }
  get password() {
    return this.loginForm.controls.password;
  }




  login() {
    //!primero verificamos si los datos de entrada son validos
    if (this.loginForm.valid) {
      //!llamamos al servicio y pasamos los valores ingresados por el usuario como un tipo
      //!loginRequest (interface en service/auth)
      this.loginService.login(this.loginForm.value as loguinRequest).subscribe({
        next: (userData) => {
          console.log(userData)
        },
        error: (errorData) => {
          console.log(errorData)
          this.loguinError = errorData;
        },
        complete: () => {
          //!ejecutamos la redireccion y el reseteo del loguin solo si se completa
          //!si handleError (del servicio) capta un error se permanecera en el login
          console.log("Login Complete")
          this.router.navigateByUrl('/products')
          this.loginForm.reset();
        }
      }) //enviamos el valor del objeto loguinForm como un loguinRequest (interface creada)

    } else {
      this.loginForm.markAllAsTouched();

    }
  }



}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta http-equiv="X-UA-Compatible" content="IE=edge">
    <meta email="viewport" content="width=device-width, initial-scale=1.0">
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.0.2/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-EVSTQN3/azprG1Anm3QDgpJLIm9Nao0Yz1ztcQTwFspd3yD65VohhpuuCOmLASjC" crossorigin="anonymous">
    <link rel="stylesheet" href="../form-login/form-login.component.css">

    <title>Ar-Salud</title>
</head>
<body>

    <div class="imagen_fija">

        <p class="texto">Inicia sesión para comenzar a comprar</p>
    </div>

      <form action="" method="POST" id="form" [formGroup]="loginForm">
        <div class= "divcontainer form container-fluid d-flex flex-column align-items-center">
            <div class="logo">
                <img src="../../assets/imagenes/logo/logo2.png" alt="">
            </div>
            <h1 class="login">Inicia sesión</h1>
            <div class="grupo">
                <input type="text" name="" id="name" formControlName="email"><span class="barra"></span>
                <label for="">Usuario o email</label>
                <!-- mensaje personalizado -->
                <div *ngIf="email.invalid && (email.dirty || email.touched)" class="text-danger">

                   <div *ngIf="email.errors?.['required']">
                     EL email es requerido.
                   </div>

                   <div *ngIf="email.errors?.['email']">
                     Mail invalido
                   </div>

                </div>
            </div>

            <div class="grupo">
                <input type="Password" id="password" formControlName="password"><span class="barra"></span>
                <label for="">Contraseña</label>
                <!-- !bindeamos la variable error declarada en la clase
                 ! componente. A esta se le asigna errorData -->
                <p class="text-bg-danger">{{loguinError}}</p>

                <div *ngIf="password.invalid && (password.dirty || password.touched)" class="text-danger">

                  <div *ngIf="password.errors?.['required']">
                    El password es requerido.
                  </div>

                </div>
            </div>

            <button type="submit" (click)="login()">Iniciar Sesión</button>
            <a routerLink="/form-recuperar" class="recuperar" href="">¿Olvidaste tu contraseña?</a>
        </div>
    </form>


</body>
